char* Trim(char* p)
{
  if (*p != '\0') {
    while ((*p != '\0') && isblank(*p)) { p++; }
    char* q = p + strlen(p) -1;
    while ((q >= p) && isblank(*q)) { q--; }
    q++;
    *q = '\0';
  }
  return p;
}

char* RemoveAllSpaces(char* p)
{

  char *cursor = p;
  uint32_t offset = 0;
  while (1) {
    *cursor = *(cursor + offset);
    if ((' ' == *cursor) || ('\t' == *cursor) || ('\n' == *cursor)) {
      offset++;
    } else {
      if (0 == *cursor) { break; }
      cursor++;
    }
  }
  return p;
}

