float ConvertTemp(float c)
{
  float result = c;

  global_update = uptime;
  global_temperature_celsius = c;

  if (!isnan(c) && Settings.flag.temperature_conversion) {
    result = c * 1.8 + 32;
  }
  result = result + (0.1 * Settings.temp_comp);
  return result;
}

float ConvertTempToCelsius(float c)
{
  float result = c;

  if (!isnan(c) && Settings.flag.temperature_conversion) {
    result = (c - 32) / 1.8;
  }
  result = result + (0.1 * Settings.temp_comp);
  return result;
}

