void cpy2lf(char *dst,uint32_t dstlen, char *src) {
  for (uint32_t cnt=0; cnt<dstlen; cnt++) {
    if (src[cnt]==0 || src[cnt]=='\n') {
      dst[cnt]=0;
      break;
    }
    dst[cnt]=src[cnt];
  }
}







