uint8_t toPercentageCR2032(uint32_t voltage) {
  uint32_t percentage;
  if (voltage < 2100) {
      percentage = 0;
  } else if (voltage < 2440) {
      percentage = 6 - ((2440 - voltage) * 6) / 340;
  } else if (voltage < 2740) {
      percentage = 18 - ((2740 - voltage) * 12) / 300;
  } else if (voltage < 2900) {
      percentage = 42 - ((2900 - voltage) * 24) / 160;
  } else if (voltage < 3000) {
      percentage = 100 - ((3000 - voltage) * 58) / 100;
  } else if (voltage >= 3000) {
      percentage = 100;
  }
  return percentage;
}






int32_t encodeSingleAttribute(class SBuffer &buf, double val_d, const char *val_str, uint8_t attrtype) {
  uint32_t len = Z_getDatatypeLen(attrtype);
  uint32_t u32 = val_d;
  int32_t i32 = val_d;
  float f32 = val_d;

  switch (attrtype) {

    case Zbool:
    case Zuint8:
    case Zenum8:
    case Zdata8:
    case Zmap8:
      buf.add8(u32);
      break;

    case Zuint16:
    case Zenum16:
    case Zdata16:
    case Zmap16:
      buf.add16(u32);
      break;

    case Zuint32:
    case Zdata32:
    case Zmap32:
    case ZUTC:
      buf.add32(u32);
      break;


    case Zint8:
      buf.add8(i32);
      break;
    case Zint16:
      buf.add16(i32);
      break;
    case Zint32:
      buf.add32(i32);
      break;

    case Zsingle:
      uint32_t *f_ptr;
      buf.add32( *((uint32_t*)&f32) );
      break;

    case Zstring:
    case Zstring16:
      {
        if (nullptr == val_str) { return -2; }
        size_t val_len = strlen(val_str);
        if (val_len > 32) { val_len = 32; }
        len = val_len + 1;
        buf.add8(val_len);
        if (Zstring16 == attrtype) {
          buf.add8(0);
          len++;
        }
        for (uint32_t i = 0; i < val_len; i++) {
          buf.add8(val_str[i]);
        }
      }
      break;

    default:
      return -1;
  }
  return len;
}
