uint16_t mp3_task_timer;
TaskHandle_t mp3_task_h;

void mp3_task(void *arg) {
  while (1) {
    while (mp3->isRunning()) {
      if (!mp3->loop()) {
        mp3->stop();
        mp3_delete();
        vTaskDelete(mp3_task_h);

      }
    }
  }
}
