uint16_t VEML7700TranslateItMs (uint8_t ittime){
  switch (ittime) {
    case 0: return 100;
    case 1: return 200;
    case 2: return 400;
    case 3: return 800;
    case 8: return 50;
    case 12: return 25;
    default: return 0xFFFF;
  }
}

